INSERT INTO `sys_log` VALUES (3160, '文章管理', '新增', 'root', '171.212.186.178', '四川 成都', 0, 'com.blog.controller.ArticleController.publish()', '[{\"articleContent\":\"**人这一生中很多当时难以消化的情绪都会有被时间所消弭的一天。\\n只有丢脸不一样，丢脸的情绪绝对经得起时间的考验，历久弥新，越酿越有味。\\n不管它过去多少年，只要想起丢人的这个事，我立马能臊得满脸通红，浑身发烫。\\n爱恨情深都能烟消云散，丢脸却能跟我形影不离日夜相伴。\\n想不起来还好，一旦想到，内心只有一个冲动: “我的老天爷啊，人活短短一世为何要受丢脸所折磨，我杀我自己。 ”**\",\"articleCover\":\"http://45.204.4.56:9000/haibara-blog/article/articleCover/b03f4eb7-6e02-4b60-9a4c-2775cdb8f8bb.jpg\",\"articleTitle\":\"关于丢脸...\",\"articleType\":1,\"categoryId\":12,\"isTop\":0,\"status\":1,\"tagId\":[14,15]}]', 'POST', NULL, '{\"code\":200,\"msg\":\"success\"}', '/article/publish', 55, '发布文章', '2025-08-04 12:05:36', '2025-08-04 12:05:36', 0);
